import React from 'react';

interface Annotation {
  id: string;
  imageId: string;
  type: 'bounding_box' | 'segmentation';
  coordinates: {
    x: number;
    y: number;
    width?: number;
    height?: number;
    points?: Array<{x: number, y: number}>;
  };
  label: string;
  confidence?: number;
  createdAt: string;
  updatedAt: string;
}

interface ImageCanvasProps {
  imageUrl: string;
  annotations: Annotation[];
  selectedTool: 'select' | 'bounding_box' | 'sam_point';
  onAnnotationCreate: (annotation: Omit<Annotation, 'id' | 'createdAt' | 'updatedAt'>) => void;
  onAnnotationUpdate: (id: string, annotation: Partial<Annotation>) => void;
  onAnnotationDelete: (id: string) => void;
}

const ImageCanvas: React.FC<ImageCanvasProps> = ({
  imageUrl,
  annotations,
  selectedTool,
  onAnnotationCreate,
  onAnnotationUpdate,
  onAnnotationDelete
}) => {
  return (
    <div style={{ 
      border: '1px solid #ccc', 
      borderRadius: '4px', 
      overflow: 'hidden',
      minHeight: '600px',
      backgroundColor: '#f0f0f0',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      flexDirection: 'column',
      gap: '1rem'
    }}>
      <div style={{
        padding: '2rem',
        textAlign: 'center',
        color: '#666'
      }}>
        <h3 style={{ margin: '0 0 1rem 0' }}>Image Canvas</h3>
        <p style={{ margin: '0 0 1rem 0' }}>
          Selected Tool: <strong>{selectedTool.replace('_', ' ').toUpperCase()}</strong>
        </p>
        <div style={{
          width: '400px',
          height: '300px',
          border: '2px dashed #ccc',
          borderRadius: '8px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          backgroundColor: '#fafafa',
          margin: '0 auto'
        }}>
          {imageUrl ? (
            <img 
              src={imageUrl} 
              alt="Canvas content"
              style={{
                maxWidth: '100%',
                maxHeight: '100%',
                objectFit: 'contain'
              }}
            />
          ) : (
            <div style={{ color: '#999' }}>
              Canvas will display here<br />
              (Fabric.js canvas coming soon)
            </div>
          )}
        </div>
        <p style={{ margin: '1rem 0 0 0', fontSize: '0.9rem' }}>
          Annotations: {annotations.length}
        </p>
      </div>
    </div>
  );
};

export default ImageCanvas;
